import React from 'react';

import back from '../img/back.svg';

const FooterApplicationForm = (props) => {

    return (
            <div className="large-12 cell">
                { props.step !==1 && (
                    <a className="back" onClick = { props.previousStep } > 
                        <img src={ back } alt="back" /> Go back to previous step
                    </a>
                )}
                { props.step <= 2  && <button type="submit" className="button" name="button">Save &amp; Continue</button> }
                { props.step > 2  && <button type="submit" className="button" name="button">Submit Application</button> }
                    {   
                        props.saving && <img alt="Loading..." src="data:image/gif;base64," />
                    }
            </div>
        )
}

export default FooterApplicationForm;
